'use strict';

angular.module('thesisApp')
  .factory('Vendor', function ($resource) {
    return $resource('/api/vendors/:id/:controller', {
      id: '@_id'
    },
    {
      changePassword: {
        method: 'PUT',
        params: {
          controller:'password'
        }
      },
      get: {
        method: 'GET',
        params: {
          id:'me'
        }
      }
	  });
  });